  }
}
/*====================================================
    common styles
====================================================*/
html {
  color: #505050;
  line-height: 1.75em;
  background: #ebebeb;
  position: relative;
  font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", "PingFang SC", "Microsoft YaHei", "Source Han Sans SC", "Noto Sans CJK SC", "WenQuanYi Micro Hei", sans-serif;
}
body {
  color: #505050;
  line-height: 1.75em;
  background: #ebebeb;
  position: relative;
  font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", "PingFang SC", "Microsoft YaHei", "Source Han Sans SC", "Noto Sans CJK SC", "WenQuanYi Micro Hei", sans-serif;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  font-weight: 400;
  color: #303030;
}
h1 {
  font-size: 2em;
}
h2 {
  font-size: 1.5em;
}
h3 {
  font-size: 1.2em;
}
h4 {
  font-size: 1em;
}
h5 {
  font-size: 0.75em;
}
h6 {
  font-size: 0.5em;
}
a {
  color: #e67e22;
  outline: none;
}
a:hover {
  color: #e67e22;
}
a:active {
  outline: none;
}
a:focus {
  outline: none;
}
a img {
  outline: none;
}
.btn {
  padding: 7px 14px;
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
}
.btn-default {
  border: 1px solid #e67e22;
  background: #e67e22;
  color: #fff;
  -webkit-transition: all 0.2s ease-in-out;
  transition: all 0.2s ease-in-out;
}
.btn-default:hover {
  border: 1px solid #303030;
  background: #303030;
  color: #fff;
}
.btn-default:focus {
  outline: none;
}
.btn-default[disabled] {
  border: 1px solid #303030;
  background: #303030;
  color: #fff;
}
input[type="search"] {
  box-sizing: border-box;
  width: 75%;
  height: 32px;
  line-height: 16px;
  padding: 7px 11px 7px 7px;
  border-radius: 4px 0 0 4px;
  margin-bottom: 1em;
  border: 1px solid #ebebeb;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
input[type="search"]:focus {
  border: 1px solid #e67e22;
  outline: none;
}
input[type="text"] {
  padding: 7px 7px;
  border: 1px solid #ebebeb;
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
input[type="text"]:focus {
  border: 1px solid #e67e22;
  outline: none;
}
input[type="url"] {
  padding: 7px 7px;
  border: 1px solid #ebebeb;
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
input[type="url"]:focus {
  border: 1px solid #e67e22;
  outline: none;
}
input[type="email"] {
  padding: 7px 7px;
  border: 1px solid #ebebeb;
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
input[type="email"]:focus {
  border: 1px solid #e67e22;
  outline: none;
}
textarea {
  padding: 7px 7px;
  border: 1px solid #ebebeb;
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
textarea:focus {
  border: 1px solid #e67e22;
  outline: none;
}
blockquote {
  border-left: 4px solid #e67e22;
  background-color: #f9f9f9;
}
pre {
  padding: 0;
  background: none;
  border: none;
}
::-moz-selection {
  color: #fff;
  background: #e67e22;
  text-shadow: none;
}
::selection {
  color: #fff;
  background: #e67e22;
  text-shadow: none;
}
.ie {
  position: fixed;
  z-index: 9999;
  left: 0;
  top: 0;
  width: 100%;
  height: 70px;
  text-align: center;
  line-height: 70px;
  background-color: #f9f9f9;
  color: #f0ad4e;
  border-bottom: 1px solid #303030;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}
#process {
  position: fixed;
  width: 0;
  height: 2px;
  background-color: #e67e22;
  top: -1px;
  left: 0;
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  -webkit-transition: width 1s ease;
  transition: width 1s ease;
  z-index: 99;
}
.carousel {
  margin-bottom: 20px;
}
.carousel img {
  width: 100%;
}
/*====================================================
    header
====================================================*/
.main-header {
  position: relative;
  text-align: center;
  line-height: 0;
  height: 180px;
  width: 100%;
  background: #fff url(http://7xpw2b.com1.z0.glb.clouddn.com/hexo-sinppet/img/banner.jpg);
  background-repeat: repeat;
  overflow: hidden;
}
.main-header .banneriframe {
  width: 100%;
  height: 100%;
  border-width: 0px;
}
.main-header .main-header-box {
  position: relative;
  width: 100%;
  padding-top: 30px;
}
.main-header .main-header-box a.header-avatar img {
  width: 82px;
  height: 82px;
  max-width: 100%;
  border-radius: 50%;
}
.main-header .main-header-box a.header-avatar:hover {
  text-decoration: none;
}
.main-header .main-header-box .branding {
  padding-top: 15px;
}
.main-header .main-header-box .branding h2 {
  font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", "PingFang SC", "Microsoft YaHei", "Source Han Sans SC", "Noto Sans CJK SC", "WenQuanYi Micro Hei", sans-serif;
  width: 320px;
  margin: 0 auto;
  letter-spacing: 3px;
  color: #e67e22;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  background-image: -webkit-linear-gradient(left, #cddc39, #e67e22 25%, #cddc39 50%, #e67e22 75%, #cddc39) !important;
  -webkit-text-fill-color: transparent!important;
  -webkit-background-clip: text!important;
  background-size: 200% 100%!important;
  animation: masked-animation 2s infinite linear !important;
}
.main-header .main-header-box .branding h2:hover {
  animation: masked-animation 1s infinite linear !important;
  text-decoration: none;
}
.home-template .main-header {
  padding-top: 62px;
  padding-bottom: 62px;
  background-repeat: no-repeat;
  background-position: center 20%;
  -webkit-background-size: cover;
  background-size: cover;
}
/* main-navigation */
.main-navigation {
  text-align: center;
  background: #fff;
  border-top: 1px solid #ebebeb;
  margin-bottom: 20px;
  border-bottom: 2px solid #e1e1e1;
}
.main-navigation .navbar-collapse {
  border-top: 1px solid #ebebeb;
}
.main-navigation .navbar-brand {
  max-width: 90%;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  padding: 19px 0 0 0;
  font-size: 14px;
  color: #505050;
  display: none;
}
.main-navigation .menu {
  padding: 0;
  margin: 0;
}
.main-navigation .menu li {
  list-style: none;
  display: inline-block;
  position: relative;
}
.main-navigation .menu li a {
  color: #505050;
  line-height: 4em;
  display: block;
  padding: 0 21px;
}
.main-navigation .menu li:hover > a {
  color: #e67e22;
  text-decoration: none;
}
.main-navigation .menu li:hover ul {
  visibility: visible;
  opacity: 1;
  filter: alpha(opacity=100);
  top: 100%;
}
.main-navigation .menu li ul {
  visibility: hidden;
  background: #fff;
  text-align: left;
  padding: 7px 0px;
  margin: 0;
  position: absolute;
  left: 0;
  top: 120%;
  width: 200px;
  z-index: 999;
  opacity: 0;
  filter: alpha(opacity=0);
  -webkit-transition: all 0.2s ease;
  transition: all 0.2s ease;
}
.main-navigation .menu li ul li {
  display: block;
  margin: 0;
}
.main-navigation .menu li ul li a {
  line-height: 2.5em;
  color: #505050;
}
.main-navigation .menu li ul:hover > a {
  color: #e67e22;
}
.main-navigation .menu li.nav-current {
  border-bottom: 2px solid #e67e22;
  margin-bottom: -2px;
}
.main-navigation .navbar-header {
  text-align: center;
}
.main-navigation .navbar-header i {
  height: 56px;
  line-height: 56px;
  font-size: 2em;
  cursor: pointer;
}
.main-navigation .navbar-header i:hover {
  color: #e67e22;
}
/*====================================================
    main post area
====================================================*/
#main {
  background: #fff;
  padding: 10px 0;
  margin-bottom: 10px;
}
.main-content .breadcrumb {
  padding-left: 20px;
