.widget .tag-cloud a:hover {
  color: #fff;
  background-color: #e67e22;
  border: 1px solid #e67e22;
  text-decoration: none;
}
.widget .social a {
  display: inline-block;
  text-align: center;
  width: 40px;
  height: 40px;
  line-height: 45px;
  color: #fff;
  background: #808080;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
  margin-right: 10px;
  margin-bottom: 10px;
}
.widget .social a:hover {
  background: #e67e22;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
.widget .social a:last-child {
  margin-right: 0px;
}
.widget .social a i {
  font-size: 20px;
}
.widget .friends-link a {
  margin-right: 15px;
}
.widget #search-form {
  position: relative;
  width: 100%;
}
.widget #search-form .search-form-submit {
  float: right;
  width: 25%;
  height: 32px;
  background: #e67e22;
  color: #fff;
  border-radius: 0 4px 4px 0;
  border: 1px solid #e67e22;
}
.widget #search-form #result-mask {
  position: fixed;
  left: 0;
  top: 0;
}
.widget #search-form .search-form-submit:focus {
  outline: none;
}
.widget #search-form #result-wrap {
  position: absolute;
  box-sizing: content-box;
  top: 45px;
  left: -50px;
  z-index: 6;
  width: 120%;
  padding: 20px;
  color: #333;
  background: #fff;
  border-radius: 4px;
  box-shadow: 1px 2px 2px #ccc;
  border: 1px solid #ccc;
}
.widget #search-form #result-wrap:before {
  content: '';
  position: absolute;
  display: inline-block;
  border-width: 0 12px 12px 12px;
  border-style: solid;
  border-color: #ccc transparent;
  right: 80px;
  top: -12px;
}
.widget #search-form #result-wrap:after {
  content: '';
  position: absolute;
  display: inline-block;
  border-width: 0 12px 12px 12px;
  border-style: solid;
  border-color: #fff transparent;
  right: 80px;
  top: -10px;
}
.widget #search-form #result-wrap #search-result {
  width: 100%;
  box-sizing: content-box;
  padding-right: 20px;
  margin-right: -20px;
  max-height: 400px;
  overflow-y: auto;
}
.widget #search-form #result-wrap #search-result .tips {
  text-align: center;
}
.widget #search-form #result-wrap #search-result .item {
  padding: 8px 0;
}
.widget #search-form #result-wrap #search-result .item:last-child .content {
  border-bottom: 0;
}
.widget #search-form #result-wrap #search-result .item a:hover {
  text-decoration: none;
}
.widget #search-form #result-wrap #search-result .item .title {
  line-height: 1.25;
  font-size: 16px;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  margin-bottom: 0px;
  color: #333;
  border: 0;
}
.widget #search-form #result-wrap #search-result .item .title:hover {
  color: #e67e22;
}
.widget #search-form #result-wrap #search-result .item .title b {
  margin: 0 2px;
  color: #e67e22;
}
.widget #search-form #result-wrap #search-result .item .content {
  font-size: 14px;
  color: #666666;
  padding-bottom: 10px;
  border-bottom: 1px solid #ccc;
}
.widget #search-form #result-wrap #search-result .item .content b {
  color: #e67e22;
  margin: 0 2px;
}
.widget .notification img {
  max-width: 100%;
}
/*====================================================
   toc
====================================================*/
#article-toc {
  padding-top: 1em;
  background-color: #fff;
  top: 0;
  bottom: 70px;
  overflow-y: auto;
}
#article-toc ol {
  list-style: none;
}
#article-toc .toc {
  padding-left: 2em;
}
#article-toc .toc .toc-item .toc-link {
  display: block;
  color: #333;
  text-decoration: none;
  padding: 4px 0;
  line-height: 1.25em;
  position: relative;
  width: 100%;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}
#article-toc .toc .toc-item .toc-link:hover {
  color: #e67e22;
  -webkit-transition: 0.15s;
  transition: 0.15s;
}
#article-toc .toc .toc-item.toc-level-2 > .toc-link {
  font-size: 15px;
}
/*====================================================
   copyright
====================================================*/
.copyright {
  background: #111;
  font-size: 13px;
  text-align: center;
  color: #666666;
  padding-top: 20px;
  padding-bottom: 20px;
  border-top: 1px solid #303030;
}
.copyright span {
  margin: 0 .5em;
}
.copyright a {
  color: #666666;
}
/*====================================================
   back-to-top and toc-btn
====================================================*/
#back-to-top,
#toc-btn {
  position: fixed;
  right: 30px;
  bottom: 30px;
  background-color: #959595;
  color: #fff;
  text-align: center;
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  z-index: 1;
  cursor: pointer;
}
#back-to-top:hover,
#toc-btn:hover {
  background: #e67e22;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
#back-to-top i,
#toc-btn i {
  width: 30px;
  height: 30px;
  line-height: 30px;
}
#toc-btn {
  bottom: 65px;
}
/*====================================================
    post reward
====================================================*/
.reward {
  padding: 5px 0;
}
.reward .reward-wrap {
  position: relative;
  display: block;
  font-size: 30px;
  width: 62px;
  height: 62px;
  line-height: 60px;
  margin: 0 auto;
  cursor: pointer;
  text-align: center;
  vertical-align: middle;
  color: #ff8140;
  -webkit-user-select: none;
  border: 1px solid #d9d9d9;
  -webkit-box-shadow: 0 0 2px #d9d9d9;
  -moz-box-shadow: 0 0 2px #d9d9d9;
  box-shadow: 0 0 2px #d9d9d9;
  -webkit-border-radius: 50%;
  -moz-border-radius: 50%;
  border-radius: 50%;
}
.reward .reward-wrap .reward-box {
  position: absolute;
  top: -220px;
  left: 50%;
  display: none;
  width: 350px;
  height: 200px;
  margin-left: -175px;
  padding: 15px;
  border: 1px solid #e6e6e6;
  background-color: #fff;
  -webkit-box-shadow: 0 1px 1px 1px #efefef;
  -moz-box-shadow: 0 1px 1px 1px #efefef;
  box-shadow: 0 1px 1px 1px #efefef;
}
.reward .reward-wrap .reward-box span {
  display: inline-block;
  width: 150px;
  height: 150px;
}
.reward .reward-wrap .reward-box span img {
  display: inline-block;
  float: left;
  width: 150px;
  height: 150px;
  margin: 0 auto;
  border: 0;
}
.reward .reward-wrap .reward-box span b {
  font-size: 14px;
  line-height: 26px;
  display: block;
  text-align: center;
  color: #666;
}
.reward .reward-wrap .reward-box:before,
.reward .reward-wrap .reward-box:after {
  position: absolute;
  content: '';
  border: 10px solid transparent;
}
.reward .reward-wrap .reward-box:before {
  bottom: -20px;
  left: 50%;
  margin-left: -10px;
  border-top-color: #e6e6e6;
}
.reward .reward-wrap .reward-box:after {
  bottom: -19px;
  left: 50%;
  margin-left: -10px;
  border-top-color: #fff;
}
.reward .reward-wrap:hover {
  color: #fff;
  background-color: #ff8140;
  border: 1px solid #ff8140;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
.reward .reward-wrap:hover .reward-box {
  display: block;
}
.reward .reward-tip {
  font-size: 18px;
  font-weight: 700;
  letter-spacing: 1px;
  color: #969696;
  margin: 15px auto;
  text-align: center;
  min-height: 24px;
}
/*====================================================
    timeline
====================================================*/
.timebox {
  position: relative;
  width: 100%;
  padding: 0 20px;
  margin: 20px auto;
  background: url(../img/timeline.gif) repeat-y 207px 0;
  overflow: hidden;
}
.timeline h2 {
  height: 44px;
  line-height: 44px;
  font-size: 30px;
  color: #666666;
  font-weight: bold;
  padding-left: 75px;
  margin-bottom: 25px;
  background: url(../img/timeline-clock.gif) #fff no-repeat 165px 0;
}
.timeline h2 img {
  vertical-align: -5px;
}
.timeline ul {
  list-style: none;
  margin: 0;
  padding: 0;
}
.timeline ul li {
  background: url(../img/timeline-dot.gif) no-repeat 180px 5px;
  padding-bottom: 20px;
  zoom: 1;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}
.timeline ul li:after {
  content: " ";
  display: block;
  height: 0;
  clear: both;
  visibility: hidden;
}
.timeline ul li h3 {
  float: left;
  width: 168px;
  font-size: 20px;
  color: #333;
  text-align: right;
  padding-right: 15px;
  margin: 0;
}
.timeline ul li h3 span {
  color: #666666;
  font-size: 12px;
  margin-left: 3px;
}
.timeline ul li a {
  padding-left: 41px;
  margin-top: -5px;
  font-weight: normal;
  font-size: 16px;
  color: #505050;
}
.timeline ul li a:hover {
  text-decoration: none;
  color: #e67e22;
}
@media screen and (max-width: 500px) {
  .timebox {
    background-position: 97px 0;
  }
  .timeline h2 {
    padding-left: 40%;
    background-position: 48px 0;
  }
  .timeline ul li {
    background-position: 70px 5px;
  }
  .timeline ul li h3 {
    width: 68px;
  }
}
/*====================================================
    highlight
====================================================*/
pre,
.highlight {
  overflow: auto;
  margin: 20px 0;
  padding: 0;
  color: #c5c8c6;
  font-size: 13px;
  background: #1d1f21;
  line-height: 1.6;
}
code {
  padding: 2px 4px;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  color: #666666;
  background: #eee;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}
pre code {
  padding: 0;
  color: #c5c8c6;
  background: none;
  text-shadow: none;
}
pre .meta {
  color: #b294bb;
}
pre .comment {
  color: #969896;
}
.highlight {
  -webkit-border-radius: 1px;
  -moz-border-radius: 1px;
  border-radius: 1px;
}
.highlight pre {
  border: none;
  margin: 0;
  padding: 10px 0;
}
.highlight table {
  margin: 0;
  width: auto;
  border: none;
}
.highlight td {
  border: none;
  padding: 0;
}
.highlight figcaption {
  font-size: 1em;
  color: #c5c8c6;
  line-height: 1em;
  margin-bottom: 1em;
}
.highlight figcaption a {
  float: right;
  color: #c5c8c6;
}
.highlight figcaption a:hover {
  border-bottom-color: #c5c8c6;
}
.highlight .gutter pre {
  padding-left: 10px;
  padding-right: 10px;
  color: #888f96;
  text-align: right;
  background-color: #000;
}
.highlight .code pre {
  width: 100%;
  padding-left: 10px;
  padding-right: 10px;
  background-color: #1d1f21;
}
.highlight .line {
  height: 20px;
}
.gutter {
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
}
.gist table {
  width: auto;
}
.gist table td {
  border: none;
}
pre .variable,
pre .attribute,
pre .tag,
pre .regexp,
pre .ruby .constant,
pre .xml .tag .title,
pre .xml .pi,
pre .xml .doctype,
pre .html .doctype,
pre .css .id,
pre .css .class,
pre .css .pseudo {
  color: #c66;
}
pre .number,
pre .preprocessor,
pre .built_in,
pre .literal,
pre .params,
pre .constant,
pre .command {
  color: #de935f;
}
pre .ruby .class .title,
pre .css .rules .attribute,
pre .string,
pre .value,
pre .inheritance,
pre .header,
pre .ruby .symbol,
pre .xml .cdata,
pre .special,
pre .number,
pre .formula {
  color: #b5bd68;
}
pre .title,
pre .css .hexcolor {
  color: #8abeb7;
}
pre .function,
pre .python .decorator,
pre .python .title,
pre .ruby .function .title,
pre .ruby .title .keyword,
pre .perl .sub,
pre .javascript .title,
pre .coffeescript .title {
  color: #81a2be;
}
pre .keyword,
pre .javascript .function {
  color: #b294bb;
}
/*====================================================
    scrollbar
====================================================*/
::-webkit-scrollbar {
  -webkit-appearance: none;
  height: 5px;
  width: 5px;
}
::-webkit-scrollbar-track {
  background-color: inherit;
}
::-webkit-scrollbar-thumb {
  -webkit-border-radius: 2px;
  -moz-border-radius: 2px;
  border-radius: 2px;
  background-color: rgba(1, 1, 1, 0.12);
}
::-webkit-scrollbar-thumb:hover {
  background-color: rgba(1, 1, 1, 0.22);
}
/*====================================================
    media
====================================================*/
@media screen and (max-width: 767px) {
  .main-navigation {
    text-align: left;
  }
  .main-navigation .navbar-brand {
    display: block;
  }
  .main-navigation .menu li {
    display: block;
    width: 33%;
    float: left;
  }
  .main-navigation .menu li:hover > a {
    color: #e67e22;
    text-decoration: none;
  }
  .main-navigation .menu li a {
    padding: 0;
  }
  .main-navigation .menu li ul {
    visibility: visible;
    padding: 0px 0px 0px 20px;
    margin: 0;
    position: relative;
    top: 0;
    width: 100%;
    opacity: 1;
    filter: alpha(opacity=100);
  }
  .prev-next-wrap {
    margin: 15px;
  }
  .prev-next-wrap a {
    display: block;
  }
  .post .post-head {
    text-align: left;
  }
  .post .post-head h1 {
    line-height: 1.25em;
  }
  .post .post-content p.brief {
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
  }
  .post .post-media {
    float: none;
    width: 100%;
    height: auto;
    margin: 0 0 1em 0;
  }
  .post-footer,
  #article-toc {
    display: none;
  }
  .content-wrap .m-post {
    padding: 0px;
  }
  .widget #search-form #result-wrap {
    left: -25px;
    width: 100%;
  }
}
@media screen and (min-width: 768px) {
  .nav-toggle-button {
    display: none;
  }
}
@media screen and (min-width: 1100px) {
  .post-content blockquote {
    padding-left: 20px;
    border-width: 4px;
  }
  .post-content blockquote blockquote {
    margin-left: 0;
  }
  .post-content figure img {
    margin: 0 0 4px;
  }
  .post-content figure figcaption {
    position: absolute;
    left: -172px;
    width: 150px;
    top: 0;
    text-align: right;
    margin-top: 0;
  }
  .post-content figure figcaption:before {
    width: 25%;
    margin-left: 75%;
    border-top: 1px solid #dededc;
    display: block;
    content: "";
    margin-bottom: 10px;
  }
}
